import React, { Component } from "react";

class GoogleOauth extends Component {
	//Set initial state
	constructor(props) {
		super(props);
		this.state = { isSignedIn: null };
	}

	//Setup for Oauth Vefification
	componentDidMount() {
		window.gapi.load("client:auth2", () => {
			window.gapi.client
				.init({
					clientId:
						"117768861076-afc46j8bs48nt61pkfddlq4t5d2che6i.apps.googleusercontent.com",
					scope: "email"
				})
				.then(() => {
					this.auth = window.gapi.auth2.getAuthInstance();
					this.setState({ isSignedIn: this.auth.isSignedIn.get() });

					//Linked to onAuthChange Function
					this.auth.isSignedIn.listen(this.onAuthChange);
				});
		});
	}

	//Called anytime the users auth status changes
	//Calls setState and rerenders component without page refresh
	onAuthChange = () => {
		this.setState({ isSignedin: this.auth.isSignedIn.get() });
	};

	onSignIn() {}

	onSignOut() {}

	//Helper Function to check if User is signed in or not
	renderAuthButton() {
		if (this.state.isSignedIn === null) {
			return null;
		} else if (this.state.isSignedIn) {
			return (
				<button onClick={this.onSignOut} className="ui red google button">
					<i className="google icon" />
					Sign Out
				</button>
			);
		} else {
			return (
				<button onClick={this.onSignIn} className="ui red google button">
					<i className="google icon" />
					Sign In With Google
				</button>
			);
		}
	}

	//Calls helper function in render method
	render() {
		return <div>{this.renderAuthButton()}</div>;
	}
}

export default GoogleOauth;
